changeSet.sql:
CREATE TABLE IF NOT EXISTS module_class_student ( module_class_student_id BIGSERIAL PRIMARY KEY, module_class_id BIGINT NOT NULL, student_id BIGINT NOT NULL, status VARCHAR(20) NOT NULL, retake_count INT NOT NULL DEFAULT 0, created_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP, updated_at TIMESTAMP WITH TIME ZONE NOT NULL DEFAULT CURRENT_TIMESTAMP,
CONSTRAINT fk_module_class_student_module_class FOREIGN KEY (module_class_id) REFERENCES module_class(module_class_id),
CONSTRAINT fk_module_class_student_user FOREIGN KEY (student_id) REFERENCES "user"(user_id) );
